              if (!!va(value)) {        //!!一般用来将后面的表达式转换为布尔型的数据（boolean）
                  return Reflect.set(target, key, value, proxy);
              } else {
                  throw Error(`不能设置${key}到${value}`)
              }
          } else {
              throw Error(`${key}不存在`);
          }
      }
  })
  }

