function add(x,y){
  return x*y;
}

export {add as default};
//等同于
//export default add;


//app.js
import { default as foo} from 'modules';
//等同于
//import foo from 'modules';
因为export default 本质是将后面的值赋值给default变量，所以可以直接将一个值卸载export default之后
export var a=4; //正确

export default var b=5; //错误    default变量后面就不能跟变量声明语句了
var b=4;
export default b; //正确

