export { foo as myFoo } from 'my_module';

//2：合并输出，作为公共入口

//index.js
export {db} from './db';
export {users} from './users'

//使用时只有加载index.js即可
import { db ,users } from './index';
模块的继承
//my_module_plus.js  该文件继承my_module  除了有my_module的所有属性和方法，还自己有 属性 a
