    render() {

        NavigationUtil.navigation = this.props.navigation;            //再创建下面的Tabnavigator之前将之前的navigation保存起来。这样避免在tabNavigation导航的页面无法调整之前的navigation.跳转之前的就使用存储在NavigationUtil内的

        const Tab = this._tabNavigator();

        return 
    }
}
